<?phpuse php\time\Time;function test() {    $b = [1, 2, 3];    for ($i = 0; $i < 100000000; $i++) {        $xa = $b[$i];        $xc = $b[$i];        $xb = $b[$i];    }    return $xb + $xa + $xc;}function test2() {    $a = ['x' => 1, 'y' => 2, 'z' => 3];    $x = 'x';    $y = 'y';    $z = 'z';    for ($i = 0; $i < 100000000; $i++) {        $xa = $a[$x];        $xc = $a[$y];        $xb = $a[$z];    }    return $xb + $xa + $xc;}$t = Time::millis();var_dump(test2());echo Time::millis() - $t, "ms";